INSERT INTO`tbl_entries_data_67` (`id`, `entry_id`, `handle`, `value`, `value_formatted`, `word_count`, `handle-en`, `value-en`, `value_formatted-en`, `word_count-en`, `handle-pl`, `value-pl`, `value_formatted-pl`, `word_count-pl`) VALUES (217, 53, 'warsztaty-plastyczne-poprzedzone-krotkim-pokazem-wybranych-basniowych-przykladow architektury-z calego-swiata-podczas-warsztatow dzieci-dowiedza-sie-na-czym-polega-technika-kolazu-wykorzystujac-rozne-materialy-m-in-fragmenty-tkanin-kolorowy-papier', 'Warsztaty plastyczne poprzedzone krótkim pokazem wybranych „baśniowych” przykładów architektury z całego świata. Podczas warsztatów dzieci dowiedzą się, na czym polega technika kolażu. Wykorzystując różne materiały (m.in. fragmenty tkanin, kolorowy papier, papier gazetowy, bibułę, guziki, muszle itp.) wykonają własne projekty baśniowych budowli.\r\n\r\nCzas trwania lekcji: około 1 godzina.', '<p>Warsztaty plastyczne poprzedzone krótkim pokazem wybranych „baśniowych” przykładów&#160;architektury z&#160;całego świata. Podczas warsztatów&#160;dzieci dowiedzą się, na czym polega technika kolażu. Wykorzystując różne materiały (m.in. fragmenty tkanin, kolorowy papier, papier gazetowy, bibułę, guziki, muszle itp.) wykonają własne projekty baśniowych budowli.</p>\n\n<p>Czas trwania lekcji: około&#160;1 godzina.</p>\n', 46, 'art-workshop-preceded-by-a brief-demonstration-of-some-fairy-tale-examples-of-architecture-from-around-the-world-during-the-workshop-children-learn-what-about-collage-then-using-a variety-of-materials-including-pieces-of-fabric-colored-paper-newspape', 'Art workshop preceded by a brief demonstration of some \"fairy tale\" examples of architecture from around the world. During the workshop, children learn what about collage. Then, using a variety of materials (including pieces of fabric, colored paper, newspaper, tissue paper, buttons, shells, etc.), they perform their own project of a fairy-tale building.\r\n\r\nLesson duration: about 1 hour.', '<p>Art workshop preceded by a&#160;brief demonstration of some &#8220;fairy tale&#8221; examples of architecture from around the world. During the workshop, children learn what about collage. Then, using a&#160;variety of materials (including pieces of fabric, colored paper, newspaper, tissue paper, buttons, shells, etc.), they perform their own project of a&#160;fairy-tale building.</p>\n\n<p>Lesson duration: about 1 hour.</p>\n', 57, 'warsztaty-plastyczne-poprzedzone-krotkim-pokazem-wybranych-basniowych-przykladow architektury-z calego-swiata-podczas-warsztatow dzieci-dowiedza-sie-na-czym-polega-technika-kolazu-wykorzystujac-rozne-materialy-m-in-fragmenty-tkanin-kolorowy-papier', 'Warsztaty plastyczne poprzedzone krótkim pokazem wybranych „baśniowych” przykładów architektury z całego świata. Podczas warsztatów dzieci dowiedzą się, na czym polega technika kolażu. Wykorzystując różne materiały (m.in. fragmenty tkanin, kolorowy papier, papier gazetowy, bibułę, guziki, muszle itp.) wykonają własne projekty baśniowych budowli.\r\n\r\nCzas trwania lekcji: około 1 godzina.', '<p>Warsztaty plastyczne poprzedzone krótkim pokazem wybranych „baśniowych” przykładów&#160;architektury z&#160;całego świata. Podczas warsztatów&#160;dzieci dowiedzą się, na czym polega technika kolażu. Wykorzystując różne materiały (m.in. fragmenty tkanin, kolorowy papier, papier gazetowy, bibułę, guziki, muszle itp.) wykonają własne projekty baśniowych budowli.</p>\n\n<p>Czas trwania lekcji: około&#160;1 godzina.</p>\n', 46);
