INSERT INTO`tbl_entries_data_67` (`id`, `entry_id`, `handle`, `value`, `value_formatted`, `word_count`, `handle-en`, `value-en`, `value_formatted-en`, `word_count-en`, `handle-pl`, `value-pl`, `value_formatted-pl`, `word_count-pl`) VALUES (189, 86, 'w trakcie-zajec-uczestnicy-poznaja-charakterystyczne-cechy-architektury-barokowej-utrwaleniu-wiadomosci-pomoze-gra-typu-memo-towarzyszacy-zajeciom-warsztat-bedzie-mial-forme-eksperymentu-pokazujacego-trudna-prace-malarza-freskow-uczniowie-naucza-sie-m', 'W trakcie zajęć uczestnicy poznają charakterystyczne cechy architektury barokowej. Utrwaleniu wiadomości pomoże gra typu Memo. Towarzyszący zajęciom warsztat będzie miał formę eksperymentu pokazującego trudną pracę malarza fresków. Uczniowie nauczą się między innymi korzystać przy odwzorowywaniu rysunku z tzw. przepruchy.\r\n\r\nCzas trwania zajęć: około 2 godziny.', '<p>W&#160;trakcie zajęć uczestnicy poznają charakterystyczne cechy architektury barokowej. Utrwaleniu wiadomości pomoże gra typu Memo. Towarzyszący zajęciom warsztat będzie miał formę eksperymentu pokazującego trudną pracę malarza fresków. Uczniowie nauczą się między innymi korzystać przy odwzorowywaniu rysunku z&#160;tzw. przepruchy.</p>\n\n<p>Czas trwania zajęć: około&#160;2 godziny.</p>\n', 44, 'during-the-lesson-participants-learn-about-the-characteristics-of-baroque-architecture-the-message-is-facilitated-by-the-use-of-memo-educational-game-the-workshop-accompanying-the-talk-takes-the-form-of-an-experiment-showing-the-hard-work-of-a fresco-p', 'During the lesson participants learn about the characteristics of Baroque architecture. The message is facilitated by the use of Memo educational game. The workshop accompanying the talk takes the form of an experiment showing the hard work of a fresco painter. The students learn, among other things, drawing reproduction techniques on a practical level.\r\n\r\nLesson duration: about 2 hours.', '<p>During the lesson participants learn about the characteristics of Baroque architecture. The message is facilitated by the use of Memo educational game. The workshop accompanying the talk takes the form of an experiment showing the hard work of a&#160;fresco painter. The students learn, among other things, drawing reproduction techniques on a&#160;practical level.</p>\n\n<p>Lesson duration: about 2 hours.</p>\n', 58, 'w trakcie-zajec-uczestnicy-poznaja-charakterystyczne-cechy-architektury-barokowej-utrwaleniu-wiadomosci-pomoze-gra-typu-memo-towarzyszacy-zajeciom-warsztat-bedzie-mial-forme-eksperymentu-pokazujacego-trudna-prace-malarza-freskow-uczniowie-naucza-sie-m', 'W trakcie zajęć uczestnicy poznają charakterystyczne cechy architektury barokowej. Utrwaleniu wiadomości pomoże gra typu Memo. Towarzyszący zajęciom warsztat będzie miał formę eksperymentu pokazującego trudną pracę malarza fresków. Uczniowie nauczą się między innymi korzystać przy odwzorowywaniu rysunku z tzw. przepruchy.\r\n\r\nCzas trwania zajęć: około 2 godziny.', '<p>W&#160;trakcie zajęć uczestnicy poznają charakterystyczne cechy architektury barokowej. Utrwaleniu wiadomości pomoże gra typu Memo. Towarzyszący zajęciom warsztat będzie miał formę eksperymentu pokazującego trudną pracę malarza fresków. Uczniowie nauczą się między innymi korzystać przy odwzorowywaniu rysunku z&#160;tzw. przepruchy.</p>\n\n<p>Czas trwania zajęć: około&#160;2 godziny.</p>\n', 44);
